import streamlit as stfrom summarizer import summarize_text, generate_abstractive_summaryfrom stt import youtube_to_textfrom preprocessing import preprocess_text, load_vectorizerfrom classifier import load_classifier, predict_topicst.set_page_config(page_title="YouTube Video Summarizer", page_icon="🎬")st.title("🎬 YouTube Video Summarizer")video_url = st.text_input("Paste the YouTube link here:")mode = st.selectbox(    "Choose summarization mode:",    ("Extractive", "Abstractive"))if st.button("Generate Summary"):    if video_url:        with st.spinner('Working on it...'):            # 1. Download and Transcribe            transcript = youtube_to_text(video_url)            # 2. Preprocess            cleaned_text = preprocess_text(transcript)            # 3. Vectorize            vectorizer = load_vectorizer()            X_new = vectorizer.transform([cleaned_text])            # 4. Classify            classifier = load_classifier()            predicted_topic = predict_topic(classifier, X_new)            # 5. Summarize            if mode == "Extractive":                summary = summarize_text(transcript)            else:                summary = generate_abstractive_summary(transcript)        # Display results        st.success("🎉 Done! (please accept me in the project division 👉👈🥺)")        st.subheader("🎯 Predicted Topic:")        st.write(predicted_topic)        st.subheader("📝 Summary:")        st.write(summary)    else:        st.error("Please insert a valid YouTube link.")